mapper.update:
UPDATE
	users
SET
	enable = #{enabled}
WHERE
	pn = #{pn}

choose.otherwise:
UPDATE
	user_seller
SET
	shop_url = #{shopUrl}
WHERE
	pn = #{pn}

mapper.select:
SELECT
	u.pn, u.id, u.name, u.enable, u.salt
FROM
	user_customer uc,
	users u 
WHERE
	uc.pn = u.pn

AND	u.enable = #{enabled}


AND	u.name = #{name}


AND	u.id = #{userId}

ORDER BY
	u.salt DESC	
LIMIT #{pagination.itemSeqBegin}, #{pagination.numItemsPerPage}

SELECT
	COUNT(u.pn)
FROM
	user_customer uc,
	users u 
WHERE
	uc.pn = u.pn

AND	u.enable = #{enabled}


AND	u.name = #{name}


AND	u.id = #{userId}
