mapper.select:
SELECT product_pn, seller_pn, image_pn, product_name, url, price, save_name, original_name, content_type, SUM(day_count) AS day_count, heart_count , customer_pn FROM (SELECT usp.pn AS product_pn, usci.seller_pn, image_pn, usp.NAME AS product_name, url, price, save_name, original_name, content_type, day_count, heart_count, pcl.customer_pn FROM user_seller_product usp JOIN user_seller_contract_info usci ON usp.seller_pn = usci.seller_pn JOIN user_seller us ON us.pn = usci.seller_pn JOIN users u ON u.pn = us.pn JOIN partnership p ON p.seller_pn = us.pn LEFT OUTER JOIN statistic_product_click spc ON spc.product_pn = usp.pn LEFT OUTER JOIN image img ON img.pn = image_pn LEFT OUTER JOIN count_product_heart cph ON cph.product_pn = usp.pn LEFT OUTER JOIN product_count_love pcl ON pcl.product_pn = usp.pn
AND pcl.customer_pn = #{customerPn}
JOIN new_product_bool npb ON npb.product_pn = usp.pn
WHERE usp.NAME IS NOT NULL AND CURDATE() BETWEEN usci.start_date AND usci.end_date AND usp.deleted ='N'
AND p.category_pn = #{categoryPn}
AND npb.new_product > 0
AND u.enable = 1 ) tb GROUP BY product_pn ORDER BY day_count DESC LIMIT #{percentCount}

mapper.delete:
DELETE FROM
	product_count_love
WHERE 
	customer_pn = #{customerPn}
AND
	product_pn = #{productPn}
