mapper.select:
SELECT p.pn, p.name, p.email, p.phone_number, p.category_pn, p.content, p.process, p.deposit, p.input_date, p.update_date, p.note, u.pn AS seller_pn, u.shop_pn, u.shop_url, u.id, u.enable, u.salt, u.name AS seller_name, u.begin_start_date, u.contract_end_date, ua.pn AS admin_pn, ua.name AS admin_name FROM partnership p LEFT OUTER JOIN ( SELECT us.pn, us.shop_pn, us.shop_url, u.id, u.enable, u.salt, u.name, uscie.contract_end_date, uscib.begin_start_date FROM user_seller us LEFT OUTER JOIN( SELECT seller_pn, end_date AS contract_end_date FROM user_seller_contract_info WHERE DATE_FORMAT(SYSDATE(), '%Y-%m-%d') BETWEEN start_date AND end_date ) uscie ON us.pn = uscie.seller_pn LEFT OUTER JOIN (
SELECT seller_pn, MIN(start_date) AS begin_start_date FROM user_seller_contract_info WHERE DATE_FORMAT(SYSDATE(), '%Y-%m-%d') < start_date GROUP BY seller_pn
) uscib ON us.pn = uscib.seller_pn , users u WHERE us.pn = u.pn ) u ON u.pn = p.seller_pn LEFT OUTER JOIN ( SELECT u.pn, u.name FROM user_admin ua, users u WHERE ua.pn = u.pn AND u.enable = TRUE ) ua ON ua.pn = p.admin_pn WHERE 1 = 1
AND process = #{process}
AND deposit = #{deposit}
AND category_pn = #{categoryPn}
AND email LIKE #{searchEmail}
AND phone_number LIKE #{searchPhoneNumber}
AND u.id LIKE #{searchSellerId}
AND u.shop_url LIKE #{searchShopUrl}
AND u.name LIKE #{searchSellerName}
AND u.shop_pn = #{shopPnNo}
AND u.enable = #{enabled}
AND admin_pn IS NULL
AND admin_pn = #{adminPn}
ORDER BY p.pn DESC

SELECT COUNT(*) FROM partnership p LEFT OUTER JOIN ( SELECT us.pn, us.shop_pn, us.shop_url, u.id, u.enable, u.salt, u.name, uscie.contract_end_date, uscib.begin_start_date FROM user_seller us LEFT OUTER JOIN( SELECT seller_pn, end_date AS contract_end_date FROM user_seller_contract_info WHERE DATE_FORMAT(SYSDATE(), '%Y-%m-%d') BETWEEN start_date AND end_date ) uscie ON us.pn = uscie.seller_pn LEFT OUTER JOIN (
SELECT seller_pn, MIN(start_date) AS begin_start_date FROM user_seller_contract_info WHERE DATE_FORMAT(SYSDATE(), '%Y-%m-%d') < start_date GROUP BY seller_pn
) uscib ON us.pn = uscib.seller_pn , users u WHERE us.pn = u.pn ) u ON u.pn = p.seller_pn LEFT OUTER JOIN ( SELECT u.pn, u.name FROM user_admin ua, users u WHERE ua.pn = u.pn AND u.enable = TRUE ) ua ON ua.pn = p.admin_pn WHERE 1 = 1
AND process = #{process}
AND deposit = #{deposit}
AND category_pn = #{categoryPn}
AND email LIKE #{searchEmail}
AND phone_number LIKE #{searchPhoneNumber}
AND u.id LIKE #{searchSellerId}
AND u.shop_url LIKE #{searchShopUrl}
AND u.name LIKE #{searchSellerName}
AND u.shop_pn = #{shopPnNo}
AND u.enable = #{enabled}
AND admin_pn IS NULL
AND admin_pn = #{adminPn}

mapper.update:
UPDATE
	partnership
SET
	update_date = SYSDATE(),
	
		name = #{name}
	
	
		email = #{email}
	
	
		phone_number = #{phoneNumber}
	
	
		process = #{process}			
	
	
		deposit = #{deposit}
	
	
		category_pn = #{categoryPn}
	
	
		note = #{note}
	
WHERE
	pn = #{pn}

UPDATE partnership SET update_date = SYSDATE(),
admin_pn = null
admin_pn = #{value}
WHERE pn = #{key}

UPDATE partnership SET update_date = SYSDATE(),
seller_pn = null
seller_pn = #{value}
WHERE pn = #{key}

mapper.select:
SELECT q.pn, qc.name AS category_name, qs.name AS section_name, q.name, q.email, q.browser, q.title, q.content, q.input_date FROM question q, question_category qc, question_section qs WHERE qc.pn = qs.category_pn AND q.section = qs.pn
AND qc.pn = #{categoryPn}
AND qs.pn = #{sectionPn}
ORDER BY q.input_date DESC

SELECT COUNT(q.pn) FROM question q, question_category qc, question_section qs WHERE qc.pn = qs.category_pn AND q.section = qs.pn
AND qc.pn = #{categoryPn}
AND qs.pn = #{sectionPn}
