mapper.insert:
INSERT INTO
	interest_section
	(
		category_pn, name, upper_name
	)
VALUES
	(
		#{categoryPn}, #{name}, #{upperName}
	)

	SELECT LAST_INSERT_ID()

INSERT INTO
	user_seller_interest
	(
		seller_pn, section_pn
	)
VALUES
	(
		#{sellerPn}, #{sectionPn}
	)

mapper.select:
SELECT
	COUNT(u.pn)
FROM
	user_seller us,
	users u
WHERE
	us.pn = u.pn

AND	u.enable = #{enabled}


AND	u.name = #{name}


AND	u.id = #{userId}

SELECT u.pn, u.name, us.shop_url, u.id, u.enable, uscit.contract_count, uscit.contract_end_date FROM user_seller us LEFT OUTER JOIN( SELECT usci.seller_pn, COUNT(usci.seller_pn) AS contract_count, now.contract_end_date FROM user_seller_contract_info usci LEFT OUTER JOIN( SELECT seller_pn, end_date AS contract_end_date FROM user_seller_contract_info WHERE SYSDATE() BETWEEN start_date AND end_date ) now ON usci.seller_pn = now.seller_pn GROUP BY usci.seller_pn ) uscit ON us.pn = uscit.seller_pn , users u WHERE us.pn = u.pn
AND u.enable = #{enabled}
AND u.name = #{name}
AND u.id = #{userId}
ORDER BY u.pn DESC

mapper.update:
UPDATE
	user_seller
SET
	shop_url = #{shopUrl}
WHERE
	pn = #{pn}

UPDATE
	users
SET
	enable = #{enabled}
WHERE
	id = #{username}

mapper.select:
SELECT
	COUNT(u.pn)
FROM
	user_customer uc,
	users u 
WHERE
	uc.pn = u.pn

AND	u.enable = #{enabled}


AND	u.name = #{name}


AND	u.id = #{userId}

SELECT u.pn, u.id, u.name, u.enable, u.salt FROM user_customer uc, users u WHERE uc.pn = u.pn
AND u.enable = #{enabled}
AND u.name = #{name}
AND u.id = #{userId}
ORDER BY u.salt DESC
