mapper.select:
SELECT p.pn, p.name, p.email, p.phone_number, p.category_pn, p.content, p.process, p.deposit, p.input_date, p.update_date, p.note, u.pn AS seller_pn, u.shop_url, u.id, u.enable, u.salt, u.name AS seller_name, u.contract_count, u.contract_end_date, ua.pn AS admin_pn, ua.name AS admin_name FROM partnership p LEFT OUTER JOIN ( SELECT us.pn, us.shop_url, u.id, u.enable, u.salt, u.name, uscit.contract_count, uscit.contract_end_date FROM user_seller us LEFT OUTER JOIN( SELECT usci.seller_pn, COUNT(usci.seller_pn) AS contract_count, now.contract_end_date FROM user_seller_contract_info usci LEFT OUTER JOIN( SELECT seller_pn, end_date AS contract_end_date FROM user_seller_contract_info WHERE SYSDATE() BETWEEN start_date AND end_date ) now ON usci.seller_pn = now.seller_pn GROUP BY usci.seller_pn ) uscit ON us.pn = uscit.seller_pn , users u WHERE us.pn = u.pn ) u ON u.pn = p.seller_pn LEFT OUTER JOIN ( SELECT u.pn, u.name FROM user_admin ua, users u WHERE ua.pn = u.pn AND u.enable = TRUE ) ua ON ua.pn = p.admin_pn WHERE 1 = 1
AND process = #{process}
AND deposit = #{deposit}
AND category_pn = #{categoryPn}
AND email LIKE #{searchEmail}
AND phone_number LIKE #{searchPhoneNumber}
AND u.id LIKE #{searchSellerId}
AND u.shop_url LIKE #{searchShopUrl}
AND u.name LIKE #{searchSellerName}
AND u.enable = #{enabled}
AND admin_pn = #{adminPn}
ORDER BY p.pn DESC

SELECT COUNT(*) FROM partnership p LEFT OUTER JOIN ( SELECT us.pn, us.shop_url, u.id, u.enable, u.salt, u.name, uscit.contract_count, uscit.contract_end_date FROM user_seller us LEFT OUTER JOIN( SELECT usci.seller_pn, COUNT(usci.seller_pn) AS contract_count, now.contract_end_date FROM user_seller_contract_info usci LEFT OUTER JOIN( SELECT seller_pn, end_date AS contract_end_date FROM user_seller_contract_info WHERE SYSDATE() BETWEEN start_date AND end_date ) now ON usci.seller_pn = now.seller_pn GROUP BY usci.seller_pn ) uscit ON us.pn = uscit.seller_pn , users u WHERE us.pn = u.pn ) u ON u.pn = p.seller_pn LEFT OUTER JOIN ( SELECT u.pn, u.name FROM user_admin ua, users u WHERE ua.pn = u.pn AND u.enable = TRUE ) ua ON ua.pn = p.admin_pn WHERE 1 = 1
AND process = #{process}
AND deposit = #{deposit}
AND category_pn = #{categoryPn}
AND email LIKE #{searchEmail}
AND phone_number LIKE #{searchPhoneNumber}
AND u.id LIKE #{searchSellerId}
AND u.shop_url LIKE #{searchShopUrl}
AND u.name LIKE #{searchSellerName}
AND u.enable = #{enabled}
AND admin_pn = #{adminPn}

mapper.update:
UPDATE
	partnership
SET
	update_date = SYSDATE(),
	
		name = #{name}
	
	
		email = #{email}
	
	
		phone_number = #{phoneNumber}
	
	
		process = #{process}			
	
	
		deposit = #{deposit}
	
	
		category_pn = #{categoryPn}
	
	
		note = #{note}
	
WHERE
	pn = #{pn}

UPDATE partnership SET update_date = SYSDATE(),
admin_pn = null
admin_pn = #{value}
WHERE pn = #{key}

UPDATE partnership SET update_date = SYSDATE(),
seller_pn = null
seller_pn = #{value}
WHERE pn = #{key}
