mapper.select:
SELECT us.pn, u.id, u.name, us.shop_url, us.shop_pn, us.notice, us.long_notice, ssid.view_count, clsp.love_count, ccsp.comment_count, bdl.banner_first, bdl.banner_second, pnb.new_product, us.hot AS love_hot_count
, cl.customer_pn
FROM user_seller us LEFT OUTER JOIN users u ON us.pn = u.pn LEFT OUTER JOIN count_love_seller_pn clsp ON us.pn = clsp.seller_pn LEFT OUTER JOIN sum_statistic_interval_7day ssid ON us.pn = ssid.seller_pn LEFT OUTER JOIN count_comment_seller_pn ccsp ON us.pn = ccsp.seller_pn LEFT OUTER JOIN banner_diff_least bdl ON us.pn = bdl.seller_pn LEFT OUTER JOIN product_new_bool pnb ON us.pn = pnb.seller_pn
LEFT OUTER JOIN count_love cl ON us.pn = cl.seller_pn AND cl.customer_pn = #{customerPn}
WHERE us.pn = #{properNumber}

SELECT 'love' AS name,COUNT(seller_pn) AS count FROM count_love WHERE seller_pn = #{properNumber} AND input_date BETWEEN #{beforeDate} AND #{nowDate}
UNION
SELECT 'view' AS name, SUM(day_count) AS count FROM `statistic_view` WHERE seller_pn = #{properNumber} AND count_date BETWEEN #{beforeDate} AND #{nowDate}
UNION
SELECT 'comment' AS name, COUNT(seller_pn) AS count FROM user_customer_comment WHERE seller_pn = #{properNumber} AND input_date BETWEEN #{beforeDate} AND #{nowDate}

SELECT usin.save_name FROM user_seller_image_name usin
WHERE usin.seller_pn = #{properNumber}
AND usin.image_pn = #{imagePn}

mapper.update:
UPDATE
	user_seller
SET
	notice = #{notice}
WHERE
	pn = #{pn}

UPDATE
	user_seller
SET
	long_notice = #{longNotice}
WHERE
	pn = #{pn}
